import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-add-contact',
  templateUrl: './add-contact.component.html',
  styleUrls: ['./add-contact.component.scss']
})
export class AddContactComponent implements OnInit {

  constructor() { }

  ngOnInit(): void {
  }

}
<div class="contacts">
    <div>
        <div>
            <img src="../../../assets/img/emptyContacts.svg" alt="Нет контактов">
        </div>
        <div class="contacts_info">
            <div class="contacts_empty-text">Нет контактов</div>
            <div class="contacts_empty-button mt-12">
                <button routerLink="/add-contact/add-form-contact">+ Добавить новый контакт</button>
            </div>
        </div>
    </div>

</div>
