import { Route } from '@angular/compiler/src/core';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { switchMap, map, tap } from 'rxjs/operators';
import { UsersService } from 'src/app/services/users.service';

@Component({
  selector: 'app-edit-contact',
  templateUrl: './edit-contact.component.html',
  styleUrls: ['./edit-contact.component.scss'],
})
export class EditContactComponent implements OnInit {
  public form: FormGroup;
  private userId: number;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private usersService: UsersService,
    private fb: FormBuilder
  ) {}

  ngOnInit(): void {
    this.initUserId();
    this.initForm();
  }

  initUserId() {
    this.route.params.subscribe(({ id }) => this.userId = +id );
  }

  initForm() {
    this.form = this.fb.group({
      username: '',
      phone: '',
      email: '',
      website: '',
    });
    this.form.patchValue(this.usersService.getUser( this.userId ))
  }

  updateUser(){
    this.usersService.updateUser( this.userId, this.form.value )
    this.router.navigate(['list-contact'])
  }

}
<div class="edit-content">
    <div class="container">
        <div class="row">
            <form action="" class="form-edit" [formGroup]="form">
                <div class="field">
                    <label for="fname">Username</label>
                    <input formControlName="username" type="text">
                </div>
                <div class="field">
                    <label for="phone">Phone number</label>
                    <input formControlName="phone" type="text">
                </div> 
                <div class="field">
                    <label for="email">Email</label>
                    <input formControlName="email" type="text">
                </div>
                <div class="field">
                    <label for="compN">Company name</label>
                    <input formControlName="website" type="text">
                </div>
            </form>
        </div>


        <div class="row">
            <div class="form-edit_buttons">
                <button class="button_back" routerLink="/list-contact">Назад</button>
                <button class="button_edit" (click)="updateUser()">Редактировать</button>
            </div>
        </div>

    </div>
</div>
