import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { UsersService } from 'src/app/services/users.service';

@Component({
  selector: 'app-add-form-contact',
  templateUrl: './add-form-contact.component.html',
  styleUrls: ['./add-form-contact.component.scss'],
})
export class AddFormContactComponent implements OnInit {
  public form: FormGroup;

  constructor(
    private usersService: UsersService,
    private fb: FormBuilder,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.initForm();
  }

  initForm() {
    this.form = this.fb.group({
      username: '',
      phone: '',
      email: '',
      website: '',
    });
  }

  addContact() {
    this.usersService.addUser( this.form.value )
    this.router.navigate(['list-contact'])
  }
}
<div class="container">
    <div class="panel">
        <div class="panel-header">
            <div class="panel-header_label">
                Добавить новый контакт
            </div>
            <div class="panel-header_button_cancel">
                <button><img src="../../../assets/img/cancel.svg" alt=""></button>
            </div>
        </div>
        <div class="panel-content">
            <div class="container">
                <div class="row">
                    <form action="" class="form-edit" [formGroup]="form">
                        <div class="field">
                            <label for="fname">Username</label>
                            <input formControlName="username" type="text">
                        </div>
                        <div class="field">
                            <label for="phone">Phone number</label>
                            <input formControlName="phone" type="text">
                        </div> 
                        <div class="field">
                            <label for="email">Email</label>
                            <input formControlName="email" type="text">
                        </div>
                        <div class="field">
                            <label for="compN">Company name</label>
                            <input formControlName="website" type="text">
                        </div>
                    </form>
                </div>


                <div class="row">
                    <div class="form_buttons">
                        <button routerLink="/add-contact" class="button_cancel">Отменить</button>
                        <button class="button_save" (click)="addContact()">Сохранить контакт</button>
                </div>
            </div>
        </div>
    </div>

</div>
